import Content from "./components/Content";
import { useState } from "react";
import { useImmer } from "use-immer";
import './styles/App.css';

const App = () =>{
    const [folders, setFolders] = useState([{name: 'folder', id: 0}])
    const [notesInQuickNotes, setNotesInQuickNotes] = useState([{text:'Note1', id: 0}]);
    const [notesInFolders, setNotesInFolders] = useImmer([{id: 0, notes: ['00000', '0']}, {id: 1, notes: ['11111', '1']}])

    function createQuickNote(text){
        setNotesInQuickNotes([...notesInQuickNotes, {text: text}])
    }
    function deleteQuickNote(id){
        let qn = folders.filter(qnote=>qnote.id != id);
        setNotesInQuickNotes(qn)

    }
    function deleteFolder(id){
        let fl = folders.filter(folder=>folder.id != id);
        setFolders(fl)
        deleteNoteInFolder(id)
    }
    function deleteNoteInFolder(id){
        let n = notesInFolders.filter(notes=>notes.id != id);
        setNotesInFolders(n)
    }
    function createFolder(text){
        const id = folders.length + 1;
        setFolders([...folders, {name: text, id: id}]);
        setNotesInFolders([...notesInFolders, {id: id, notes:[] }])

    }

    return(
        <Content folders={folders} deleteQuickNote={deleteQuickNote} deleteFolder={deleteFolder} setFolders={setFolders} createFolder={createFolder} createQuickNote={createQuickNote} notesInQuickNotes={notesInQuickNotes} setNotesInFolders={setNotesInFolders} notesInFolders={notesInFolders}/>
    )
}

export default App
