import { useState } from "react";

const CreateFolderPage = (props) =>{
    const [folderName, setFolderName] = useState('')
    let id = props.folders.length+1;
    return(<div>
        <div className='CFPBlock'>
            <p>Crete your new folder</p>
            <div className='block'><input onChange={(e)=>{setFolderName(e.target.value)}} ></input></div>
            <div className='block'>
                <button className='CFPBut font' onClick={()=>{props.setFolders([...props.folders, {name: folderName, id: id}]); setIsCreate(false); props.setNotesInFolders([...props.notesInFolders, {id: id, notes:[] }])}}>Confirm</button>
            </div>
        </div>
    </div>)
}

export default CreateFolderPage
