import Content from "./components/Content";
import { useState } from "react";
import { useImmer } from "use-immer";

const App = () =>{
    const [folders, setFolders] = useState([{name: 'folder', id: 0}])
    const [notesInQuickNotes, setNotesInQuickNotes] = useState([{text:'Note1'}, {text:'Note1'}, {text:'Note1'}, {text:'Note1'}]);
    const [notesInFolders, setNotesInFolders] = useImmer({id: 0, notes: ['00000', '0']}, {id: 1, notes: ['11111', '1']})

    function createQuickNote(text){
        setNotesInQuickNotes(draft=>draft.push({text: text}))
    }

    return(
        <Content folders={folders} setFolders={setFolders} createQuickNote={createQuickNote} notesInQuickNotes={notesInQuickNotes} setNotesInFolders={setNotesInFolders} notesInFolders={notesInFolders}/>
    )
}

export default App
