import Body from "./Body"
import Header from "./Header"
import Footer from "./Footer"
import CreateFolderPage from "./CreateFolderPage"
import FolderScreen from "./FolderScreen"
import cl from '../styles/Content.module.css'
import { useState } from "react"


const Content = (props) =>{
    const [isCreate, setIsCreate] = useState(false);
    const [isInFolder, setIsInFolder] = useState(false)
    const [sd, setSd] = useState(0)


    function createFolder(){
        setIsCreate(true)
    }

    return(<div className={cl.Content}>
        <Header />
        {isCreate ? <CreateFolderPage setIsCreate={setIsCreate} setFolders={props.setFolders} setNotesInFolders={props.setNotesInFolders} notesInFolders={props.notesInFolders}/> 
        : (isInFolder ? <FolderScreen sd={sd} setNotesInFolders={props.setNotesInFolders} setIsInFolder={setIsInFolder}/>
            :<Body setSd={setSd} folders={props.folders} createQuickNote={props.createQuickNote} notesInQuickNotes={props.notesInQuickNotes} createFolder={createFolder}/>)}
        <Footer />
    </div>)
}

export default Content
