import QuickNote from "../UI/QuickNote"
import Addqn from "./Addqn"
import cl from '../styles/QuickNotesBlock.module.css'
import {
    CSSTransition,
    TransitionGroup,
  } from 'react-transition-group';

const QuickNotesBlock = (props) =>{

    return(<div className={cl.QuickNotesBlock}> 
        <div></div>
        <Addqn createQuickNote={props.createQuickNote}/>
        <div></div>
        <TransitionGroup>

        {props.notesInQuickNotes.map(note=><CSSTransition key={note.id} timeout={500} classNames={cl.note}>
            <QuickNote  deleteQuickNote={props.deleteQuickNote} id={note.id} text={note.text}/>
            </CSSTransition>)} 
        </TransitionGroup>
    </div>)

}

export default QuickNotesBlock
