import './App.css';
import { useState } from 'react';
import {useImmer} from 'use-immer'





/*{name: 'folder', id: 0},{name: 's', id: 1}*/

function App(){
    const [sd, setSd] = useState(0)
    const [inFolder, setInFolder] = useState(false)
    const [folder, setFolder] = useState([])
    const [fn, setFn] = useState('');
    const [value, setValue] = useState('');
    const [nif, setNif] = useImmer([{id: 0, notes: []}])
    const [note, setNote] = useState([])
    const [isCreate, setIsCreate] = useState(false)
    function handleAddqn(){
        setNote([...note, {text:value}])
    }
    function handleInput(e){setValue(e.target.value)}

    return(<div className='App'>
    <Content note={note} sd={sd} setNif={setNif} nif={nif} value={value} 
    setSd={setSd} setInFolder={setInFolder} 
    setValue={setValue} inFolder={inFolder} 
    folder={folder} fn={fn} setFn={setFn} 
    setFolder={setFolder} setIsCreate={setIsCreate} 
    isCreate={isCreate}handleAddqn={handleAddqn} 
    handleInput={handleInput}/>    
    </div>)
}



function Content({handleAddqn,folder,fn, nif, setNif, setSd, setValue, inFolder, sd, setFn, setFolder, note, setInFolder, setIsCreate, isCreate,value, handleInput}) {
  return ( <div className='Content'>
  <Header />
  {isCreate ? <CreateFolderPage setNif={setNif} nif={nif} folder={folder} setIsCreate={setIsCreate} fn={fn} setFn={setFn} setFolder={setFolder}/>
  :  (inFolder ? <FolderScreen setNif={setNif} nif={nif} value={value} setInFolder={setInFolder} setValue={setValue} sd={sd} setFolder={ setFolder} folder={folder}/>
    :<Body note={note} nif={nif} setSd={setSd} folder={folder} setInFolder={setInFolder} value={value} setIsCreate={setIsCreate} handleAddqn={handleAddqn} handleInput={handleInput} />)}
  <Footer />
    </div>
  );
}




function Header(){
    return(<div className='Header'>

    </div>)
}


function CreateFolderPage({folder, fn, setFn, setIsCreate, nif, setNif, setFolder}){
    let id = folder.length+1;
    return(<div className='CreateFolderPage'><div className='CFPBlock'>
        <p>Crete your new folder</p>
        <div className='block'><input onChange={(e)=>{setFn(e.target.value)}} ></input></div>
        <div className='block'><button className='CFPBut' onClick={()=>{setFolder([...folder, {name: fn, id: id}]); setIsCreate(false); setNif([...nif, {id: id, notes:[] }])}}>Confirm</button>
        </div>
        </div></div>) 
}

function Body({handleAddqn, folder, nif, setSd, note, setInFolder, setIsCreate, value, handleInput}){
    return(<div className='Body'>

    <QuickNotesBlock note={note} value={value} handleAddqn={handleAddqn} handleInput={handleInput} />
    <FoldersBlock nif={nif} setSd={setSd} folder={folder} setInFolder={setInFolder} setIsCreate={setIsCreate} />
    </div>)
}

function FolderScreen({folder, sd, nif, setNif,  setFolder, setInFolder, value, setValue}){
    const n = nif.filter((el) => el.id == sd)
    const new_n = n.map(n=>n.notes.map(e=><button>{e}</button>))
    /*const list = [
        ...nif,
         {...nif[sd],
         notes:
          [...nif[sd].notes, value]}
    ];*/
    return(<div className='FolderScreen'>
        <input className='FolderScreenInput' onChange={e=>{setValue(e.target.value)}}></input>
        <button className='FolderScreenButton' onClick={()=>setNif(draft=>{draft[sd].notes = [...draft[sd].notes, value]})}>Add new note</button>

        <button className='Back' onClick={()=>setInFolder(false)}>Back</button>
        <div>{new_n}</div>
    </div>)
}
function QuickNotesBlock({handleAddqn, note,value,  handleInput}){
  const qnl = note.map(note=><input className='AddqnInput' value={note.text}></input>)
  return(<div className='QuickNotesBlock'>
  {qnl}  
  <Addqn handleAddqn={handleAddqn} value={value} handleInput={handleInput} />

  </div>)
}
function Addqn({handleAddqn,value, handleInput}){
  return(<div className='Addqn'>
      <input className='AddqnInput' value={value} onChange={handleInput}></input>
      <button className='AddqnBut' onClick={handleAddqn}>+</button>
  </div>)
}

function FoldersBlock({setIsCreate, setSd, setInFolder,nif, folder}){
  return(<div className='FoldersBlock'>
  <Addfl nif={nif} setIsCreate={setIsCreate}/>
  <Folders setSd={setSd} setInFolder={setInFolder} folder={folder}/>
  </div>)
}
function Addfl({setIsCreate, nif}){
  return(<div className='Addfl'>
      <button className='Addflbut' onClick={()=>{setIsCreate(true); console.log(nif)}}><text>+</text></button>
  </div>)
}
function Folders({folder, setSd, setInFolder}){
  const f = folder.map(folder=><button className='folder' onClick={()=>{setInFolder(true); setSd(folder.id)}}>{folder.name}</button>)
  return(<div className='Folders'>
      {f}
  </div>)
}



function Footer(){
  return(<div className='Footer'>
  <a className='Contacts' href=''>About us</a>
  <a className='Contacts' href=''>Facebook</a>
  <a className='Contacts' href=''>Instagram</a>
  <a className='Contacts' href=''>Telegram</a>
  </div>)
}


export default App;
